'CREATE OR REPLACE FORCE EDITIONABLE VIEW "V_DASHB_QUERY" ("NOW", "BEGIN", "END", "NAME", "DESCR", "RN", "RM", "ID", "UNIX_MILLIS", "TS", "TS_E", "TS_IB", "TS_IE", "SEC", "SEC_TOT", "WEIGHT", "CID", "VAL", "DATA", "STAT", "STAT_COLOR", "VAL_LAST", "VA'
||'L_FIRST", "TS_DAY", "TS_MONTH", "TS_YEAR", "ITS", "ITS_E", "INTERVALMINUTES_LI") AS ',
'  with',
'/*',
'** Two-Step Query:',
'**',
'** STEP 1: retrieve all relevant value records',
'** STEP 2: split value records at interval boundaries and fill in additional records',
'**',
'** Uses parameter injection to make sure the parameters are applied to step 1',
'**',
'** The query parameters MUST be set using the following procedure:',
'**',
'**  dashb_query_pkg.set_parameters(''current week'', ''60'', ''DB_FRA_USAGE'') is not null',
'*/',
'/*',
'** STEP 0: single record subquery containing all relevant context variables',
'*/',
'ENV as',
' ( SELECT * from V_DASHB_QUERY_ENV',
' ),',
'/*',
'** list of sensors (multiple sensors can be queried by specifying multiple space-separated IDs in ENV.SensorID)',
'*/',
'S as',
' ( select ENV.RN,',
'          ENV.NOW,',
'          nvl(ENV.tsFrom, ENV.BEGIN) as BEGIN,',
'          nvl(ENV.tsFrom, ENV.END  ) as END,',
'          nvl(ENV.from_Millis, ENV.BEGIN_MILLIS) as BEGIN_MILLIS,',
'          nvl(ENV.to_Millis,   ENV.END_MILLIS  ) as END_MILLIS,',
'          ENV.intervalMinutes,',
'          ENV.second,',
'          S.*',
'   from   V_DASHB_QUERY_ENV ENV,',
'          DASHB_SENSOR      S',
'   where  S.ID       in (select COLUMN_VALUE from table(apex_string.split(ENV.SensorID,'' '')))',
'   --or   S.CATEGORY in (select COLUMN_VALUE from table(apex_string.split(ENV.Category,'' '')))',
'     or   S.CATEGORY  = ENV.Category',
' ),',
'/*',
'** row generator',
'*/',
'RG as',
' ( SELECT level as rn FROM dual CONNECT BY LEVEL <= 1000',
' ),',
'/*',
'** STEP 1: retrieve all relevant value records',
'*/',
'Q_VAL as',
' (select   /* all non-current values */',
'           /*+ QB_NAME(MIDDLE_SENSOR_VALUES)',
'               USE_NL(S V1)',
'               INDEX(V1 DASHB_SENSOR_VAL_I_ID_UM)',
'               INLINE',
'           */',
'           V1.SENSOR_ID,',
'           V1.CID,',
'           V1.TS,',
'           V1.VAL,',
'           V1.SEC,',
'           V1.DATA,',
'           V1.STAT,',
'           V1.UNIX_MILLIS,',
'           S.COLOR,',
'           S.LAST_CID,',
'           S.ORDER_1,',
'           S.ORDER_2,',
'           S.ORDER_3,',
'           S.ORDER_4,',
'           V1.TS + numToDsInterval(V1.SEC, second) as ts_e,',
'           APP_UTIL.TRUNC_TS(V1.TS, intervalMinutes)                                    as its,',
'           APP_UTIL.TRUNC_TS(V1.TS + numToDsInterval(V1.SEC, second), intervalMinutes)  as its_e',
'  from     S',
'    join   DASHB_SENSOR_VAL     V1',
'      on   V1.SENSOR_ID       = S.ID',
'     and   V1.UNIX_MILLIS       between S.BEGIN_MILLIS and S.END_MILLIS - 1',
'  --where    V1.CID            != S.LAST_CID',
'  UNION ALL',
'  select   /* the non-current value which begins before the interval and ends in the interval */',
'           /*+ QB_NAME(FIRST_SENSOR_VALUE)',
'               USE_NL(S V2)',
'               INDEX(V2 DASHB_SENSOR_VAL_I_ID_UME)',
'               INLINE',
'             */',
'           V2.SENSOR_ID,',
'           V2.CID,',
'           V2.TS,',
'           V2.VAL,',
'           V2.SEC,',
'           V2.DATA,',
'           V2.STAT,',
'           V2.UNIX_MILLIS,',
'           S.COLOR,',
'           S.LAST_CID,',
'           S.ORDER_1,',
'           S.ORDER_2,',
'           S.ORDER_3,',
'           S.ORDER_4,',
'           V2.TS + numToDsInterval(V2.SEC, second) as ts_e,',
'           APP_UTIL.TRUNC_TS(V2.TS, intervalMinutes)                                    as its,',
'           APP_UTIL.TRUNC_TS(V2.TS + numToDsInterval(V2.SEC, second), intervalMinutes)  as its_e',
'  from     S',
'    join   DASHB_SENSOR_VAL     V2',
'      on   V2.SENSOR_ID       = S.ID',
'     and   V2.UNIX_MILLIS     < S.BEGIN_MILLIS',
'     and   V2.UNIX_MILLIS_E   > S.BEGIN_MILLIS -- between S.BEGIN_MILLIS and S.END_MILLIS - 1',
'  where    V2.CID            != S.LAST_CID',
'  UNION ALL',
'  select   /* the current value */',
'           /*+ QB_NAME(CURRENT_SENSOR_VALUE)',
'               USE_NL(S V3)',
'               INDEX(V3 DASHB_SENSOR_VAL_I_SENSOR_CID)',
'               INLINE',
'             */',
'           V3.SENSOR_ID,',
'           V3.CID,',
'           V3.TS,',
'           V3.VAL,',
'           app_util.secondsBetween(V3.TS, S.NOW, 6) as SEC,',
'           V3.DATA,',
'           V3.STAT,',
'           V3.UNIX_MILLIS,',
'           S.COLOR,',
'           S.LAST_CID,',
'           S.ORDER_1,',
'           S.ORDER_2,',
'           S.ORDER_3,',
'           S.ORDER_4,',
'           S.NOW as ts_e,',
'           APP_UTIL.TRUNC_TS(V3.TS,   intervalMinutes) as its,',
'           APP_UTIL.TRUNC_TS(S.NOW,   intervalMinutes) as its_e',
'  from     S',
'    join   DASHB_SENSOR_VAL     V3',
'      on   V3.SENSOR_ID        = S.ID',
'     and   V3.CID              = S.LAST_CID',
'     and  (V3.TS         between S.BEGIN and S.END',
'      or   S.NOW         between S.BEGIN and S.END)',
' )',
'/*',
'** STEP 2: split value records at interval boundaries and fill in additional records',
'*/',
'select /*+ QB_NAME(EXPAND_ROWS) */',
'       ENV.NOW,',
'       ENV.BEGIN,',
'       ENV.END,',
'       ENV.NAME,',
'       ENV.DESCR,',
'       Q2.RN,',
'       Q2.RM,',
'       Q2.SENSOR_ID as ID,',
'       APP_UTIL.UNIX_MILLIS(Q2.TS_IB) as unix_millis,',
'       Q2.TS,',
'       Q2.TS_E,',
'       Q2.TS_IB,',
'       Q2.TS_IB + numToDsInterval(INTERVALMINUTES, minute) - numToDsInterval(0.000000001, second) as TS_IE,',
'       Q2.SEC_IN_INTERVAL as SEC,      -- seconds in this interval',
'       Q2.SEC             as SEC_TOT,  -- total seconds of this value',
'       case',
'         when nvl(IntervalMinutes_LI,0) = 0 then 1',
'         when Q2.TS_IB + numToDsInterval(Q2.SEC_IN_INTERVAL, second) = NOW then 1',
'         else (Q2.SEC_IN_INTERVAL / 60) / IntervalMinutes_LI',
'       end as WEIGHT,',
'       Q2.CID,',
'       Q2.VAL,',
'       Q2.DATA,',
'       Q2.STAT,',
'       case',
'         when Q2.STAT = ''danger''  then ''#ff0000''',
'         when Q2.STAT = ''warning'' then ''#ffff00''',
'         when Q2.STAT = ''info''    then ''#0000ff''',
'         when Q2.STAT = ''success'' then ''#00ffff''',
'         when Q2.STAT = ''normal''  then ''#e0e0e0''',
'         else Q2.COLOR',
'       end as STAT_COLOR,',
'       LAST_VALUE (Q2.VAL) OVER (PARTITION BY Q2.SENSOR_ID, Q2.TS_IB ORDER BY Q2.CID RANGE BETWEEN current row         and unbounded following) VAL_LAST,',
'       FIRST_VALUE(Q2.VAL) OVER (PARTITION BY Q2.SENSOR_ID, Q2.TS_IB ORDER BY Q2.CID RANGE BETWEEN unbounded preceding and current row        ) VAL_FIRST,',
'       to_number(to_char(Q2.TS_IB, ''DD''  )) ts_day,',
'       to_number(to_char(Q2.TS_IB, ''MM''  )) ts_month,',
'       to_number(to_char(Q2.TS_IB, ''YYYY'')) ts_year,',
'       Q2.its,',
'       Q2.its_e,',
'       Q2.IntervalMinutes_LI',
'from   ENV,',
' (select /*+ QB_NAME(MULTIPLY_ROWS) */',
'         RG.RN,',
'         Q1.ITS + numToDsInterval((nvl(RG.RN,1) - 1) * intervalMinutes, minute) as TS_IB,',
'         case when RG.RN is null  then Q1.SEC',
'              when RG.RN = 1      then APP_UTIL.secondsbetween(Q1.TS, APP_UTIL.TRUNC_TS(Q1.TS + numToDsInterval(intervalMinutes, minute), intervalMinutes), 6)',
'              when RG.RN = RM     then APP_UTIL.secondsbetween(Q1.ITS + numToDsInterval((nvl(RG.RN,1) - 1) * intervalMinutes, minute),Q1.TS_E,  6)',
'              else intervalMinutes * 60',
'         end as SEC_IN_INTERVAL,',
'         /* IntervalMinutes_LI ... limit IntervalMinutes of last interval to minutes that have actually passed */',
'         case',
'            when Q1.CID = Q1.LAST_CID and Q1.RM  = 0',
'              then APP_UTIL.minutesBetween(Q1.ITS,  Q1.TS_E, 9)',
'            when Q1.CID = Q1.LAST_CID and Q1.RM  = RG.RN',
'              then APP_UTIL.minutesBetween(Q1.ITS + numToDsInterval((nvl(RG.RN,1) - 1) * intervalMinutes, minute), Q1.TS_E, 9)',
'            else',
'              intervalMinutes',
'         end as IntervalMinutes_LI,',
'         Q1.*',
'  from   ENV,',
'   (select /*+ QB_NAME(COMPUTE_ROW_MULTIPLIER) */',
'           Q_VAL.*,',
'           case',
'              when Q_VAL.its = Q_VAL.its_e  then 0',
'              when intervalMinutes = 0      then 0',
'              else ceil(APP_UTIL.minutesbetween (Q_VAL.its, Q_VAL.ts_e, 3) / intervalMinutes)',
'           end as RM',
'     from  Q_VAL, ENV',
'   ) Q1',
'  left outer join RG on RG.RN <= Q1.RM and RG.RN < 1000',
' )     Q2',
'where  ENV.RN   = 0 -- "now" is already filtered at step 1',
'  or   Q2.TS_IB                                                  between begin and end',
'  or   Q2.TS_IB + numToDsInterval(Q2.SEC_IN_INTERVAL, second)    between begin and end',
'order by ORDER_1, ORDER_2, ORDER_3, ORDER_4, CID, RN',
';',
'',
