'  function set_parameters',
'   (period            in varchar2 default ''now'', -- null or period name from V_DASB_PERIODS., default: "now"  (return only the last row)',
'    intervalMinutes   in number   default null,  -- interval length in minutes, 60 means to aggregate values by 1 hour, 1440 means 1 day, default 1',
'    sensorID          in varchar2 default null,  -- Sensor ID, or comma-separated list of Sensor IDs',
'    category          in varchar2 default null,  -- Sensor category',
'    aggregation       in varchar2 default null,  -- null, SUM, MAX, MIN, or AVG, null (no aggregations) means all value rows will be returned. will be ignored when no period is null',
'    numPeriod         in number   default null,  -- null or period number from V_DASB_PERIODS., null means to return only the last row',
'    tsFrom            in timestamp with time zone default null,',
'    tsTo              in timestamp with time zone default null',
'   ) return varchar2 is',
'  begin',
'    begin',
'      if nvl(period, ''now'') = ''now'' then',
'        g_numPeriod := 0;',
'        g_period    := ''now'';',
'        g_From      := tsFrom;',
'        g_To        := tsTo;',
'      elsif period is not null then',
'        select rn,',
'               name,',
'               BEGIN,',
'               END',
'        into   g_numPeriod,',
'               g_period,',
'               g_from,',
'               g_to',
'        from   V_DASHB_PERIODS',
'        where  name = period',
'        ;',
'      else',
'        select rn,',
'               name,',
'               BEGIN,',
'               END',
'        into   g_numPeriod,',
'               g_period,',
'               g_from,',
'               g_to',
'        from   V_DASHB_PERIODS',
