'    category          in varchar2 default null,',
'    aggregation       in varchar2 default null,',
'    numPeriod         in number   default null,',
'    tsFrom            in timestamp with time zone default null,',
'    tsTo              in timestamp with time zone default null',
'   ) is',
'    f varchar2(1000);',
'  begin',
'    f := set_parameters',
'     (period,',
'      intervalMinutes,',
'      sensorID,',
'      category,',
'      aggregation,',
'      numPeriod,',
'      tsFrom,',
'      tsTo',
'     );',
'  end;',
'  function get_parameter_description',
'  return varchar2',
'  is',
'  begin',
'    if g_SensorID is null and g_Category is null then return ''no sensors selected''; end if;',
'    return ''''||g_SensorID||g_Category||''/''||g_period ||''/''||g_IntervalMinutes;',
