'     (new dashb_query_parameters',
'      (g_period,',
'       g_IntervalMinutes,',
'       g_SensorID,',
'       g_Category,',
'       g_Aggregation,',
'       g_numPeriod,',
'       g_from,',
'       g_to',
'      )',
'     );',
