'         CAST(''||dat||'' as DATE)  as TS_DATE,',
'         S.ICON,',
'         S.FMT,',
'         S.AGGREGATION,',
'         S.COLOR',
'from     V_DASHB_QUERY Q',
'join     DASHB_SENSOR  S on S.ID = Q.ID',
'group by Q.ID,',
'         S.NAME,',
'         Q.TS_IB,',
'         Q.TS_DAY,',
'         Q.TS_MONTH,',
'         Q.TS_YEAR,',
'         S.FMT,',
'         S.ICON,',
'         S.LINK,',
'         S.AGGREGATION,',
'         S.COLOR,',
'         S.PERIOD,',
'         S.ORDER_1, S.ORDER_2, S.ORDER_3, S.ORDER_4, Q.ID, TS_IB',
'order by S.ORDER_1, S.ORDER_2, S.ORDER_3, S.ORDER_4, Q.ID, TS_IB',
''';',
'    trc.msg(f);',
'    return f;',
'  end;',
'end dashb_query;',
