wwv_flow_imp_shared.create_install_script(
 p_id=>wwv_flow_imp.id(48757863333241803)
,p_install_id=>wwv_flow_imp.id(48754591246204200)
,p_name=>'DB Sensors'
,p_sequence=>60
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'CREATE OR REPLACE EDITIONABLE TYPE "DASHB_SENSOR_TYPE" force as object',
' (',
'  /**',
'   * Baseclass for Key Performance Indicators',
'   *',
'   * drop type DASHB_SENSOR_TYPE force;',
'   *',
'   * to update all KPIs:',
'   *',
'   * begin DASHB_SENSOR_TYPE.updateAll; end;',
'   *',
'   * einfache Jobdefinition:',
'      BEGIN',
'        DBMS_SCHEDULER.drop_job(''UPDATE_DASHBOARD_SENSORS'');',
'        DBMS_SCHEDULER.create_job (',
'          job_name        => ''UPDATE_DASHBOARD_SENSORS'',',
'          job_type        => ''PLSQL_BLOCK'',',
'          job_action      => ''BEGIN DASHB_SENSOR_TYPE.updateAll; END;'',',
'          start_date      => SYSTIMESTAMP,',
'          repeat_interval => ''freq=minutely; bysecond=10;'',',
'          enabled         => TRUE);',
'      END;',
'   *',
'   */',
'  id                   varchar2(100),',
'  name                 varchar2(100),',
'  category             varchar2(100),',
'  "TYPE"               varchar2(100),',
'  period               number,',
'  arg1                 varchar2(100),',
'  arg1_name            varchar2(100),',
'  arg2                 varchar2(100),',
'  arg2_name            varchar2(100),',
'  arg3                 varchar2(100),',
'  arg3_name            varchar2(100),',
'  arg4                 varchar2(100),',
'  arg4_name            varchar2(100),',
'  arg5                 varchar2(100),',
'  arg5_name            varchar2(100),',
'  success_from         number,',
'  success_to           number,',
'  normal_from          number,',
'  normal_to            number,',
'  info_from            number,',
'  info_to              number,',
'  warning_from         number,',
'  warning_to           number,',
'  danger_from          number,',
'  danger_to            number,',
'  last_cid             number,',
'  last_ts              timestamp with time zone,',
'  last_sec             number,',
'  last_val             number,',
'  last_stat            varchar2(10),',
'  last_data            varchar2(1000),',
'  errormessage         varchar2(4000),',
'  -- next evaluation time',
'  next_period          number,',
'  next_ts              timestamp with time zone,',
'  -- details of current valuation',
'  new_ts               timestamp with time zone,',
'  new_val              number,',
'  new_stat             varchar2(10),',
'  new_data             varchar2(1000),',
'  compute_interval     varchar2(10),',
'  keep_interval        varchar2(10),',
'  period_from          timestamp with time zone,',
'  period_to            timestamp with time zone,',
'  period_descr         varchar2(100),',
'  /**',
'   * scheduler job control',
'   */',
'  static procedure startJob,',
'  static procedure stopJob,',
'  /**',
'   * updates all KPIs',
'   */',
'  static procedure updateAll',
'   (force in number := 0',
'   ),',
'  static procedure sanitizeLastCIDs,',
'  /**',
'   * sets default values',
'   */',
'  member procedure init,',
'  /**',
'   * loads the record from DASHB_KPI',
'   */',
'  member procedure load',
'   (strID in varchar2',
'   ),',
'  /**',
'   * Computes and stores the value',
'   *',
'   * @strID: KPI ID from DASHB_KPI',
'   * @ts:    Timestamp of the computation',
'   */',
'  member procedure run',
'   (strID in varchar2',
'   ),',
'  /**',
'   * Computes the value',
'   */',
'  member procedure compute,',
'  /**',
'   * Saves the value in DASHB_KPI_VAL',
'   */',
'  member procedure saveVal,',
'  /**',
'   * calculates the next timestamp when this KPI should be computed',
'   */',
'  member function getNextTS',
'  return timestamp with time zone',
' ) NOT FINAL',
'/',
'',
'',
'CREATE OR REPLACE EDITIONABLE TYPE BODY "DASHB_SENSOR_TYPE" is',
'',
'  /*',
'  ** **************************************************************************',
'  ** scheduler job control',
'  ** **************************************************************************',
'  */',
'  static procedure startJob is',
'  begin',
'    DBMS_SCHEDULER.create_job (',
'      job_name        => ''UPDATE_DASHBOARD_SENSORS'',',
'      job_type        => ''PLSQL_BLOCK'',',
'      job_action      => ''BEGIN DASHB_SENSOR_TYPE.updateAll; END;'',',
'      start_date      => SYSTIMESTAMP,',
'      repeat_interval => ''freq=minutely; bysecond=10;'',',
'      enabled         => TRUE);',
'',
'    dbms_scheduler.set_attribute(''UPDATE_DASHBOARD_SENSORS'',''NLS_ENV'',''NLS_LANGUAGE=''''GERMAN'''' NLS_TERRITORY=''''GERMANY'''' '');',
'  end;',
'',
'  static procedure stopJob is',
'  begin',
'    DBMS_SCHEDULER.drop_job(''UPDATE_DASHBOARD_SENSORS'');',
'  end;',
'',
'',
'  /*',
'  ** **************************************************************************',
'  ** perform sensor update',
'  ** **************************************************************************',
'  */',
'  static procedure updateAll',
'   (force in number := 0',
'   )',
'  is',
'    ts timestamp with time zone := SYSTIMESTAMP;',
'    NOW GT_NOW%ROWTYPE;',
'    s  varchar2(10000);',
'    n  number;',
'    ts1 timestamp with time zone;',
'    ts2 timestamp with time zone;',
'  begin',
'',
'    /*',
unistr('    ** bewirkt, dass alle Metriken f\00FCr den selben Zeitpunkt, d.h. mit dem selben Datenbankstand berechnet werden'),
'    */',
'    SET TRANSACTION ISOLATION LEVEL SERIALIZABLE;',
'    ROLLBACK;',
'',
'    /*',
'    ** einheitlichen Zeitpunkt aller Aktualisierungen dieser Transaktion festlegen',
'    **',
'    ** GT_NOW wurde erstellt mit:',
'    **',
'    ** CREATE GLOBAL TEMPORARY TABLE GT_NOW ON COMMIT DELETE ROWS as select * from V_NOW;',
'    */',
'    insert into GT_NOW (select * from V_NOW);',
'',
'    select * into NOW from GT_NOW;',
'',
'    execute immediate ''alter session set time_zone = ''''Europe/Berlin'''''';',
'',
'    select sessiontimezone',
'    into   s',
'    from   dual;',
'',
'    --trc.info(''sessiontimezone=''||s);',
'',
'    for c in',
'     (select   *',
'      from     DASHB_SENSOR S',
'      where    S.enabled = 1',
'        and   (S.next_ts is null or S.next_ts <= NOW.NOW or force != 0)',
'      order by S.ORDER_1,',
'               S.ORDER_2,',
'               S.ORDER_3,',
'               S.ORDER_4,',
'               S.ID)',
'    loop',
'',
'      if force != 0 then',
'        trc.msg(''force update ''||c.NAME);',
'      end if;',
'',
'      begin',
'        s := ''declare O ''||c.type||'' := ''||c.type||''; begin O.run(:1); end;'';',
'        execute immediate s using c.id;',
'',
'      exception when others then',
'        trc.info(s);',
'        s := SQLERRM;',
'        update DASHB_SENSOR set ERRORMESSAGE = s where id = c.ID;',
'        trc.err(''Error updating metric "''||c.ID||''"'');',
'      end;',
'',
'    end loop;',
'',
'    commit;',
'  end;',
'',
'',
'  /*',
'  ** **************************************************************************',
'  ** Set LAST_CID of all sensors to the correct value (Change-ID with the highest timestamp)',
'  ** **************************************************************************',
'  */',
'  static procedure sanitizeLastCIDs is',
'  begin',
'    MERGE INTO DASHB_SENSOR T USING',
'      (with Q as',
'          (select   SENSOR_ID, max(TS) MAX_TS',
'           from     DASHB_SENSOR_VAL',
'           group by SENSOR_ID',
'          )',
'        select Q.SENSOR_ID, max(CID) LAST_CID',
'        from   Q join DASHB_SENSOR_VAL V on V.SENSOR_ID = Q.SENSOR_ID and V.TS = Q.MAX_TS',
'        group by Q.SENSOR_ID',
'      ) Q',
'      ON (T.ID = Q.SENSOR_ID)',
'      WHEN MATCHED THEN UPDATE',
'      SET T.LAST_CID = Q.LAST_CID',
'    ;',
'  end;',
'',
'  /*',
'  ** **************************************************************************',
'  */',
'  member procedure init',
'  is',
'  begin',
'    self := null;',
'  end;',
'',
'  /*',
'  ** **************************************************************************',
'  */',
'  member procedure load',
'   (strID in varchar2',
'   ) is',
'  begin',
'    select Q.*',
'    into   id,              -- id,',
'           name,            -- name,',
'           category,        -- category,',
'           "TYPE",          -- "TYPE",',
'           period,          -- period',
'           next_period,     -- next_period',
'           arg1,            -- arg1,',
'           arg2,            -- arg2,',
'           arg3,            -- arg3,',
'           arg4,            -- arg4,',
'           arg5,            -- arg5,',
'           arg1_name,       -- arg1_name,',
'           arg2_name,       -- arg2_name,',
'           arg3_name,       -- arg3_name,',
'           arg4_name,       -- arg4_name,',
'           arg5_name,       -- arg5_name,',
'           success_from,    -- success_from,',
'           success_to,      -- success_to,',
'           normal_from,     -- normal_from,',
'           normal_to,       -- normal_to,',
'           info_from,       -- info_from,',
'           info_to,         -- info_to,',
'           warning_from,    -- warning_from,',
'           warning_to,      -- warning_to,',
'           danger_from,     -- danger_from,',
'           danger_to,       -- danger_to,',
'           last_cid,        -- last_cid,',
'           compute_interval,-- compute_interval',
'           keep_interval,   -- keep_interval',
'           last_ts,         -- DKV.TS,',
'           last_sec,        -- DKV.SEC,',
'           last_val,        -- DKV.VAL,',
'           last_stat,       -- DKV.STAT,',
'           last_data        -- DKV.DATA',
'    from  (',
'      select id,',
'             name,',
'             category,',
'             "TYPE",',
'             period,',
'             next_period,',
'             arg1,',
'             arg2,',
'             arg3,',
'             arg4,',
'             arg5,',
'             arg1_name,',
'             arg2_name,',
'             arg3_name,',
'             arg4_name,',
'             arg5_name,',
'             success_from,',
'             success_to,',
'             normal_from,',
'             normal_to,',
'             info_from,',
'             info_to,',
'             warning_from,',
'             warning_to,',
'             danger_from,',
'             danger_to,',
'             last_cid,',
'             compute_interval,',
'             keep_interval,',
'             DSV.TS,',
'             DSV.SEC,',
'             DSV.VAL,',
'             DSV.STAT,',
'             DSV.DATA',
'      from   DASHB_SENSOR DBS left outer join DASHB_SENSOR_VAL DSV on DSV.SENSOR_ID = DBS.ID and DSV.CID = DBS.LAST_CID',
'      where  DBS.id = strID',
'      order by DSV.TS desc',
'    ) Q',
'    where rownum = 1',
'    ;',
'  end;',
'',
'  /*',
'  ** **************************************************************************',
'  */',
'  member procedure compute',
'  is',
'  begin',
'    return;',
'  end;',
'',
'  /*',
'  ** **************************************************************************',
'  */',
'  member procedure run',
'   (strID in varchar2',
'   )',
'  is',
'    f varchar2(100);',
'  begin',
'    load(strID);',
'',
'    select MINUTE AT TIME ZONE SESSIONTIMEZONE',
'    into   NEW_TS',
'    from   GT_NOW',
'    ;',
'',
'    if PERIOD is not null then',
'      begin',
'        select BEGIN,',
'               END,',
'               DESCR',
'        into   PERIOD_FROM,',
'               PERIOD_TO,',
'               PERIOD_DESCR',
'        from   V_DASHB_PERIODS',
'        where  rn = PERIOD',
'        ;',
'',
'      exception when no_data_found then null;',
'      end;',
'    end if;',
'',
'    compute();',
'',
'    NEW_STAT :=',
'    case',
'      when NEW_VAL between danger_from  and danger_to  then ''danger''',
'      when NEW_VAL between warning_from and warning_to then ''warning''',
'      when NEW_VAL between info_from    and info_to    then ''info''',
'      when NEW_VAL between success_from and success_to then ''success''',
'      when NEW_VAL between normal_from  and normal_to  then ''normal''',
'      else ''''',
'    end;',
'',
'    if NEXT_PERIOD is not null then',
'      --trc.msg(''NEXT_PERIOD=''||NEXT_PERIOD);',
'      select begin',
'      into   NEXT_TS',
'      from   V_DASHB_PERIODS where rn = NEXT_PERIOD',
'      ;',
'    end if;',
'',
'    saveVal();',
'',
'  end;',
'',
'  /*',
'  ** **************************************************************************',
'  */',
'  member procedure saveVal',
'  is',
'    n       number;',
'    AA      DASHB_SENSOR_VAL%rowtype;',
'    NOW     GT_NOW%ROWTYPE;',
'',
'    ts1     timestamp with time zone := systimestamp;',
'    s       varchar2(1000);',
'  begin',
'',
'    select * into NOW from GT_NOW;',
'',
'    if NEW_VAL is null and LAST_VAL is null then',
'      null;',
'    elsif NEW_VAL = LAST_VAL then',
'      --trc.msg (''val ''||ID||'' "''||NAME||''" unchanged: ''||NEW_VAL);',
'      null;',
'    else',
'      --trc.info(''''||ID||'' "''||NAME||''": ''||LAST_VAL||'' ==> ''||NEW_VAL);',
'      null;',
'    end if;',
'',
'    NEW_STAT :=',
'      case',
'        when NEW_VAL between danger_from  and danger_to  then ''danger''',
'        when NEW_VAL between warning_from and warning_to then ''warning''',
'        when NEW_VAL between info_from    and info_to    then ''info''',
'        when NEW_VAL between success_from and success_to then ''success''',
'        when NEW_VAL between normal_from  and normal_to  then ''normal''',
'        else null',
'      end;',
'',
'    if NEW_STAT is null and LAST_STAT is null then',
'      --trc.msg (''stat ''||ID||'' "''||NAME||''" NULL'');',
'      null;',
'',
'    elsif NEW_STAT = LAST_STAT then',
'      --trc.msg (''stat ''||ID||'' "''||NAME||''" unchanged: ''||NEW_STAT);',
'      null;',
'',
'    else',
'      s := ''stat "''||NAME||''": "''||NEW_VAL||''" stat ''||LAST_STAT||'' ==> ''||NEW_STAT;',
'      case NEW_STAT',
'        when ''danger''   then trc.err(s, errm => false, backtrace => false, callstack => false);',
'        when ''warning''  then trc.warn(s);',
'        when ''info''     then trc.info(s);',
'        when ''success''  then trc.success(s);',
'        else                 trc.info(s);',
'      end case;',
'    end if;',
'',
'    --if  KEEP_INTERVAL = ''hourly''',
'    --and LAST_TS is not null',
'    --and APP_UTIL.TRUNC_TS(LAST_TS, 60) < NOW.HOUR',
'    --then',
'    --  /*',
'    --  ** keep at least one record per hour',
'    --  */',
'    --  trc.info(''KEEP 1 per HOUR: ''||LAST_TS||'' ==> ''||APP_UTIL.TRUNC_TS(LAST_TS, 60)||'' < ''||NOW.HOUR);',
'    --',
'    --  /*',
'    --  ** SEC berechnen bis Ende der Stunde',
unistr('    --  ** todo: bei Bedarf mehrere Records einf\00FCgen'),
'    --  */',
'    --  update DASHB_SENSOR_VAL',
'    --  set    SEC = APP_UTIL.secondsBetween(TS, APP_UTIL.TRUNC_TS(TS, 60) + NOW.I_H - NOW.I_PS, 6)',
'    --  where  CID = LAST_CID',
'    --  ;',
'    --',
'    --  LAST_TS  := NOW.HOUR;',
'    --  LAST_CID := SEQ_DASHB_SENSOR_VAL.nextval;',
'    --',
'    --  insert into DASHB_SENSOR_VAL',
'    --    (CID,     SENSOR_ID, TS,      SEC, VAL, STAT, DATA)',
'    --  values',
'    --    (LAST_CID, ID,     LAST_TS, APP_UTIL.secondsBetween(NOW.HOUR, NOW.NOW, 6),   LAST_VAL, LAST_STAT, LAST_DATA);',
'    --',
'    --else',
'      --trc.msg(''UPD SECONDS: ''||APP_UTIL.secondsBetween(LAST_TS, NEW_TS, 6));',
'    update DASHB_SENSOR_VAL',
'    set    SEC = APP_UTIL.secondsBetween(TS, NEW_TS, 6)',
'    where  CID = LAST_CID',
'    ;',
'    --end if;',
'',
'',
'    if  (NEW_VAL  = LAST_VAL  or (NEW_VAL  is null and LAST_VAL  is null))',
'    and (NEW_STAT = LAST_STAT or (NEW_STAT is null and LAST_STAT is null))',
'    and (NEW_DATA = LAST_DATA or (NEW_DATA is null and LAST_DATA is null))',
'    then',
'      /*',
'      ** value didn''t change, just update stats',
'      */',
'      update DASHB_SENSOR T',
'      set    LAST_CID      = self.LAST_CID,',
'             COMPUTE_COUNT = nvl(COMPUTE_COUNT , 0) + 1,',
'             COMPUTE_MSEC  = nvl(COMPUTE_MSEC  , 0) + APP_UTIL.MSECBETWEEN(ts1, SYSTIMESTAMP),',
'             ERRORMESSAGE  = '''',',
'             NEXT_TS       = self.NEXT_TS',
'      where  T.ID = self.ID',
'      ;',
'',
'    else',
'      /*',
'      ** insert new value',
'      */',
'',
'      --trc.info(''NEW_VAL:  ''||NEW_VAL ||'' LAST_VAL:  ''||LAST_VAL);',
'      --trc.info(''NEW_STAT: ''||NEW_STAT||'' LAST_DATA: ''||LAST_STAT);',
'      --trc.info(''NEW_DATA: ''||NEW_DATA||'' LAST_DATA: ''||LAST_DATA);',
'',
'      LAST_CID := SEQ_DASHB_SENSOR_VAL.nextval;',
'',
'      insert into DASHB_SENSOR_VAL',
'       (CID, SENSOR_ID, TS, SEC, VAL, STAT, DATA)',
'      values',
'       (LAST_CID, ID, NEW_TS, 0, NEW_VAL, NEW_STAT, NEW_DATA);',
'',
'      update DASHB_SENSOR T',
'      set    LAST_CID      = self.LAST_CID,',
'             COMPUTE_COUNT = T.COMPUTE_COUNT + 1,',
'             COMPUTE_MSEC  = T.COMPUTE_MSEC + APP_UTIL.MSECBETWEEN(ts1, SYSTIMESTAMP),',
'             ERRORMESSAGE  = self.ERRORMESSAGE,',
'             NEXT_TS       = self.NEXT_TS',
'      where  T.ID = self.ID',
'      ;',
'',
'   end if;',
'  end;',
'',
'  /*',
'  ** **************************************************************************',
'  ** recalc at begin of the next hour',
'  */',
'  member function getNextTS',
'  return timestamp with time zone is',
'  begin',
'    for c in (select hour + I_H as f from V_NOW)',
'    loop',
'      return c.f;',
'    end loop;',
'  end;',
'',
'end;',
'/',
'',
'CREATE OR REPLACE EDITIONABLE TYPE "DB_SENSOR_FRA_USAGE" UNDER DASHB_SENSOR_TYPE',
' (',
'  /**',
unistr('   * F\00FCllstand der Fast Recovery Area in Prozent'),
'   */',
'  constructor function DB_SENSOR_FRA_USAGE return self as result,',
'  overriding member procedure init,',
'  overriding member procedure compute',
' )',
'/',
'',
'',
'CREATE OR REPLACE EDITIONABLE TYPE BODY "DB_SENSOR_FRA_USAGE" is',
'  constructor function DB_SENSOR_FRA_USAGE return self as result is',
'  begin',
'    return;',
'  end;',
'  /*',
'  ** **************************************************************************',
'  */',
'  overriding member procedure init',
'  is',
'  begin',
'    self := null;',
'  end;',
'  /*',
'  ** **************************************************************************',
'  */',
'  overriding member procedure compute',
'  is',
'  begin',
'    --trc.ENTER('''');',
'    select ROUND((SPACE_USED/SPACE_LIMIT)*100),',
'           '''' || round(SPACE_USED / 1073741824,1) || '' G''',
'    into   NEW_VAL,',
'           NEW_DATA',
'    from   V$RECOVERY_FILE_DEST;',
'    --trc.EXIT(''NEW_VAL=''||NEW_VAL);',
'    return;',
'  exception when no_data_found then',
'    ERRORMESSAGE := ''No Recovery Area defined!'';',
'  end;',
'end;',
'/',
'',
'CREATE OR REPLACE EDITIONABLE TYPE "DB_SENSOR_OSSTAT" UNDER DASHB_SENSOR_TYPE',
' (',
'  /*',
'  ** CPU-Auslastung in Prozent',
'  **',
'  ** ARG1: [IDLE_TIME] || [BUSY_TIME] || [USER_TIME] || [SYS_TIME]',
'  */',
'  constructor function DB_SENSOR_OSSTAT return self as result,',
'  overriding member procedure init,',
'  overriding member procedure compute',
' )',
'/',
'',
'',
'CREATE OR REPLACE EDITIONABLE TYPE BODY "DB_SENSOR_OSSTAT" is',
'  constructor function DB_SENSOR_OSSTAT return self as result is',
'  begin',
'    return;',
'  end;',
'  /*',
'  ** **************************************************************************',
'  */',
'  overriding member procedure init',
'  is',
'  begin',
'    self := null;',
'  end;',
'  /*',
'  ** **************************************************************************',
'  */',
'  overriding member procedure compute',
'  is',
'  begin',
'    --trc.msg(''''||ARG1||'' TS1:''||LAST_TS||'' TS2:''||NEW_TS||'' DAT:''||LAST_DATA);',
'    with CORES as (select VALUE as COUNT from V$OSSTAT where STAT_NAME = ''NUM_CPU_CORES'')',
'    select round((((VALUE-to_number(nvl(LAST_DATA,VALUE)))/CORES.COUNT) / 100) / app_util.SECONDSBETWEEN(LAST_TS, NEW_TS) * 100, 2),',
'           VALUE',
'    into   NEW_VAL,',
'           NEW_DATA',
'    from   V$OSSTAT, CORES',
'    where  STAT_NAME = ARG1;',
'    if NEW_VAL < 0 then',
'      NEW_VAL := null;',
'    end if;',
'  end;',
'end;',
'/',
'',
'CREATE OR REPLACE EDITIONABLE TYPE "DB_SENSOR_RANDOM" UNDER DASHB_SENSOR_TYPE',
' (',
'  /*',
'  ** gibt mit der Wahrscheinlichkeit in ARG1 einen Wert zwischen ARG2 und ARG3 aus',
'  */',
'  constructor function DB_SENSOR_RANDOM return self as result,',
'  overriding member procedure init,',
'  overriding member procedure compute',
' )',
'/',
'',
'',
'CREATE OR REPLACE EDITIONABLE TYPE BODY "DB_SENSOR_RANDOM" is',
'  constructor function DB_SENSOR_RANDOM return self as result is',
'  begin',
'    return;',
'  end;',
'  /*',
'  ** **************************************************************************',
'  */',
'  overriding member procedure init',
'  is',
'  begin',
'    self := null;',
'  end;',
'  /*',
'  ** **************************************************************************',
'  */',
'  overriding member procedure compute',
'  is',
'    n number := DBMS_RANDOM.VALUE;',
'    function tn (s varchar2, fmt varchar2 default ''99999999999990D99999999'') return number is',
'    begin',
'      return to_number(s , fmt, ''NLS_NUMERIC_CHARACTERS='''',.'''''');',
'    end;',
'  begin',
'    --trc.ENTER(''n=''||n||'' arg1:''||arg1);',
'    if n < tn(ARG1) then',
'      n := DBMS_RANDOM.VALUE;',
'      NEW_VAL := round(tn(ARG2) + (tn(ARG3) - tn(ARG2)) * n, tn(ARG4));',
'    else',
'      NEW_VAL := nvl(LAST_VAL, 0);',
'    end if;',
'    --trc.EXIT(''NEW_VAL=''||NEW_VAL);',
'    return;',
'  end;',
'end;',
'/',
'',
'CREATE OR REPLACE EDITIONABLE TYPE "DB_SENSOR_SESSIONS" UNDER DASHB_SENSOR_TYPE',
' (',
'  /**',
'   * Anzahl angemeldeter Sessions',
'   */',
'  constructor function DB_SENSOR_SESSIONS return self as result,',
'  overriding member procedure init,',
'  overriding member procedure compute',
' )',
'/',
'',
'',
'CREATE OR REPLACE EDITIONABLE TYPE BODY "DB_SENSOR_SESSIONS" is',
'  constructor function DB_SENSOR_SESSIONS return self as result is',
'  begin',
'    return;',
'  end;',
'  /*',
'  ** **************************************************************************',
'  */',
'  overriding member procedure init',
'  is',
'  begin',
'    self := null;',
'  end;',
'  /*',
'  ** **************************************************************************',
'  */',
'  overriding member procedure compute',
'  is',
'  begin',
'    --trc.ENTER('''');',
'    select count(*)',
'    into   NEW_VAL',
'    from   V$SESSION',
'    where  TYPE != ''BACKGROUND'';',
'    --trc.EXIT(''NEW_VAL=''||NEW_VAL);',
'    return;',
'  end;',
'end;',
'/',
'',
''))
